   and  block_id= #{blockId,jdbcType=BIGINT}


   and  is_encrypted=#{isEncrypted,jdbcType=TINYINT}


   and  encrypt_algorithm= #{encryptAlgorithm,jdbcType=VARCHAR}


   and  user_sign=#{userSign,jdbcType=LONGVARBINARY}


    and tsa_auth= #{tsaAuth,jdbcType=LONGVARBINARY}

