mapper.sql:
from certificate
	where 1=1

   and  auth_code = #{authCode,jdbcType=VARCHAR}


   and  ref_code = #{refCode,jdbcType=VARCHAR}


   and  serial_code = #{serialCode,jdbcType=VARCHAR}


   and  start_time = #{startTime,jdbcType=TIMESTAMP}


   and  end_time = #{endTime,jdbcType=TIMESTAMP}


   and  file_id = #{fileId,jdbcType=VARCHAR}


   and  desc_info = #{descInfo,jdbcType=VARCHAR}


   and  subject = #{subject,jdbcType=VARCHAR}


   and  issuer = #{issuer,jdbcType=VARCHAR}


   and  store_password = #{storePassword,jdbcType=VARCHAR}


   and  key_password = #{keyPassword,jdbcType=VARCHAR}


   and  valid_day = #{validDay,jdbcType=BIGINT}


    and user_id = #{userId,jdbcType=BIGINT}


   and  md5 = #{md5,jdbcType=VARCHAR}


   and  data = #{data,jdbcType=LONGVARBINARY}
